#!/usr/bin/env python3

seq = ""

ecor1_seq = "GAATTC"


ecor1_start = seq.find(ecor1_seq)
ecor1_end = seq.find(ecor1_seq) + len(ecor1_seq) - 1

# Print the start and end positions containing the first EcoR1 site
# Note that the variables are the python index numbers,
# for "human" numbers, I will add 1
print(f'''\nEcoR1 startPos: {ecor1_start+1}
endPos: {ecor1_end+1}''')
