#!/usr/bin/env python3

# Input sequence
seq_raw = ""
print(f'\nSequence: {seq_raw}')
seq_set = set(seq_raw)

# Determine the unique characters in the sequence
print(f"\nUnique characters: {seq_set}")

# Count the number of each nucleotide in the sequence
# Store in a dictionary
# Report GC content
NT_count = {}
for NT in seq_set:
	count = seq_raw.count(NT)
	NT_count[NT] = count
print(f"Nucleotide counts: {NT_count}")
GC_content = (NT_count['G'] + NT_count['C'])/len(seq_raw)
print(f"GC content: {round(GC_content*100,2)}%")
